import React, { Component } from 'react';
import Orders from './components/orders';
class App extends Component {
  state = {
    data: [],
    customer_data: [],
    product_data: []
  }

  componentDidMount() {
    fetch('https://aqueous-thicket-50958.herokuapp.com/api/v1/orders/1')
    .then(res => res.json())
    .then((data) => {
      this.setState({ data })
    })
    .catch(console.log)


    fetch('https://aqueous-thicket-50958.herokuapp.com/api/v1/customers/1')
    .then(res => res.json())
    .then(customer_data => {
      this.setState({customer_data})
    })

  }

  render() {
    return (
      <Orders customer_data={this.state.customer_data} data={this.state.data} />
    )
  }
}

export default App;
