import React, { Component } from 'react';
import Orders from './components/orders';
class App extends Component {
  state = {
    data: []
  }

  componentDidMount() {
    fetch('https://aqueous-thicket-50958.herokuapp.com/api/v1/orders/1', { mode: 'no-cors'})
    .then(res => res.json())
    .then((data) => {
      this.setState({ data })
    })
    .catch(console.log)
  }

  render() {
    return (
      <Orders data={this.state.data} />
    )
  }
}

export default App;
