import React, { Component } from 'react';
import Orders from './components/orders';
class App extends Component {
  state = {
    data: [],
    customer_data: [],
    product_data: []
  }

  componentDidMount() {
    fetch('https://aqueous-thicket-50958.herokuapp.com/api/v1/orders/1')
    .then(res => res.json())
    .then((data) => {
      this.setState({ data })
    })
    .catch(console.log)

    this.state.data.forEach(item => {
      const customer_id = item.customer_id;
      fetch('https://aqueous-thicket-50958.herokuapp.com/api/v1/customers/' + customer_id)
      .then(res => res.json())
      .then(data => {
        console.log(data);
        var extdata = this.state.data;  
        extdata['customer_data'] = data;
        this.setState({data: extdata});    
        console.log(this.state.data);
      })
    })
  }

  render() {
    return (
      <Orders data={this.state.data} />
    )
  }
}

export default App;
